import styled from "styled-components";
import { useState } from "react";
const Dot = styled.div`
  width: 15px;
  height: 15px;
  border: solid 3px #2c2c2c;
  background-color: transparent;
  border-radius: 100%;
  margin: 0px 5px;
`;





  const UISlider = (props) => {
    const [activeSlideIndex, setActiveSlideIndex] = useState(0);
    const settings = {
  dots: true,
  infinite: true,
  speed: 1000,
  slidesToShow: 1,
  autoplaySpeed: 10000,
  slidesToScroll: 1,
  autoplay: true,
  adaptiveHeight: true,
  nextArrow: <div />,
  prevArrow: <div />,
  appendDots: (dots) => (
    <div className="w-100">
      <ul className="list-unstyled d-flex flex-row justify-content-center align-items-center">
        {dots}
      </ul>
    </div>
  ),

  beforeChange(oldIndex, newIndex) {
    setActiveSlideIndex(newIndex);
  },
  customPaging: (slideIndex) => (
    <Dot key={slideIndex} active={slideIndex === activeSlideIndex}></Dot>
  ),


    responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
  }; 
};

  export default settings
